    };

    const processQRCode = (qr: string, errorMessgeOnURType: string) => {
        processUR(qr, errorMessgeOnURType);
    };

    const handleStop = () => {
        ee.emit('read', {
            status: 'canceled',
        });
    };

    const handleRetry = () => {
        reset();
    };

    const processUR = (ur: string, errorMessgeOnURType: string) => {
        try {
            if (!urDecoder.isComplete()) {
                urDecoder.receivePart(ur);
                setProgress(urDecoder.getProgress());
            } else {
                const result = urDecoder.resultUR();
                let foundExpected = false;
                expectTypes.forEach((et) => {
                    if (et === result.type) {
                        foundExpected = true;
                        ee.emit('read', {
                            result,
                            status: 'success',
                        });
                        return;
                    }
                });
                if (!foundExpected)
                    throw new URTypeError(`received ur type ${result.type}, but expected [${expectTypes.join(',')}]`);
            }
        } catch (e) {
            if (e instanceof URTypeError) {
                setError(errorMessgeOnURType);
            } else {
                setError(e.message);
            }
        }
    };

    const element = (
        <div
            style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
            }}
        >
            {title && <p style={{ fontSize: '1.25rem', fontWeight: 'bold' }}>{title}</p>}
            {description && <p style={{ fontSize: '1rem', textAlign: 'center' }}>{description}</p>}
            <Suspense fallback={<div />}>
                <div style={{ position: 'relative', width: '100%' }}>
                    {!cameraReady ? (
                        <div
                            style={{
                                position: 'absolute',
                                top: '50%',
                                left: '50%',
                                transform: 'translate(-50%, -50%)',
                            }}
                        >
                            <LoadingSpinner />
                        </div>
                    ) : null}
                    <QrReader
                        onScan={(data: any) => {
                            if (data) {
                                setCameraReady(true);
                                processQRCode(data, URTypeErrorMessage);
                            }
                        }}
                        delay={100}
                        style={{ width: '100%' }}
                        onError={(e) => {
                            setError(e.message);
                        }}
                    />
                </div>
            </Suspense>
            <p>{(progress * 100).toFixed(0)} %</p>
            {error && <p style={{ color: 'red', fontSize: '1rem' }}>{error}</p>}
            <ButtonGroup>
                <Button onClick={handleStop}>Close</Button>
                {error && <Button onClick={handleRetry}>Retry</Button>}
            </ButtonGroup>
        </div>
    );

    return [
        element,
        {
            read: (expect, options) => {
                return new Promise((resolve) => {
                    setExpectTypes(expect);
                    if (options) {
                        options.title && setTitle(options.title);
                        options.description && setDescription(options.description);
                        URTypeErrorMessage = options.URTypeErrorMessage ? options.URTypeErrorMessage : '';
                    }
                    ee.once('read', (result) => {
                        reset();
                        resolve(result);
                    });
                });
            },
            cameraReady,
        },
    ];
};

